mapper.select:
select * from t_pay

	
		p_pay_date >=
		#{startDate}
	
	
		and p_pay_date <=
		#{endDate}
	
	
		and p_loginer = #{loginer}
	

order by p_pay_date desc
limit #{start}, #{length}

select count(p_id) from t_pay

	
		p_pay_date >=
		#{startDate}
	
	
		and p_pay_date <=
		#{endDate}
	
	
		and p_loginer = #{loginer}
